function Function() {
    const [input, setInput] = useState('');
    console.log(input)

    const click = (input) => {
        console.log(input);
    }

    // '' is the initial state value
    return (
        <div>
            <label>Please specify:</label>
            <input value={input}/>
            <button onClick={click}>click</button>
        </div>
    );
}

export default Function
